package If_DecisionMaking.IfElse;

public class GreaterThan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20, b=10;
				
		if (a>b) 
			System.out.println(a+ " is greater than "+ b);
		else 
			System.out.println(a+ " is smaller than " + b);
		
		
		if (a%b == 0)
			System.out.println("a is greater than b");
		else
			System.out.println("a is smaller than b");
			
			
	}

}
